mapper.select:
SELECT t1.work_date, t1.user_id, t1.over_work, t2.user_name userName, t2.identity_no identityNo, t3.project_code projectCode, t3.project_name projectName,t4.dept_name deptName FROM t_over_work t1 LEFT JOIN t_user t2 on t2.user_id = t1.user_id LEFT JOIN t_project t3 on t3.id = t2.project_id LEFT JOIN department t4 on t4.dept_id = t2.dept_id WHERE t1.user_id = #{userId,jdbcType=VARCHAR}
AND t1.work_date>=#{startDate,jdbcType=VARCHAR}
AND t1.work_date<=#{endDate,jdbcType=VARCHAR}
ORDER BY t1.work_date DESC

SELECT t1.user_id, t1.work_date,t1.over_work,t2.user_name userName,t2.identity_no identityNo,t4.dept_name deptName, t3.project_code projectCode, t3.project_name projectName, sum(t1.over_work) sumOverWork FROM t_over_work t1 LEFT JOIN t_user t2 on t2.user_id = t1.user_id LEFT JOIN t_project t3 on t3.id = t2.project_id LEFT JOIN department t4 on t4.dept_id = t2.dept_id WHERE t1.user_id = #{userId,jdbcType=VARCHAR}
AND t1.work_date>=#{startDate,jdbcType=VARCHAR}
AND t1.work_date<=#{endDate,jdbcType=VARCHAR}
ORDER BY t1.work_date DESC
